import { createRouter, createWebHashHistory } from 'vue-router'
import ManualTasks from '../views/ManualTasks.vue'
import ApiTasks from '../views/ApiTasks.vue'
import CombinedView from '../views/CombinedView.vue'

const routes = [
  {
    path: '/',
    redirect: '/manual'
  },
  {
    path: '/manual',
    name: 'manual',
    component: ManualTasks
  },
  {
    path: '/api',
    name: 'api',
    component: ApiTasks
  },
  {
    path: '/combined',
    name: 'combined',
    component: CombinedView
  }
]

const router = createRouter({
  history: createWebHashHistory(),
  routes
})

export default router
